
public class TypeConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		double f;
		f=5/4;		//정수 형태의 연산 후 -> double 형태로 바꿈
		System.out.println(f);
		
		f=(double)5/4;
		System.out.println(f);
		
		i=(int)1.3+(int)1.8;
		System.out.println(i);
	
	}

}
